import React, {useState, useEffect} from "react"
import axios from "axios"
import Header from "./Header"
import Footer from "./Footer"
import Blog from "./Blog"

function App(){

    const [blog,setBlog] = useState({title:"", content:""})
    const [blogs,setBlogs] = useState([])
    const [createPost, setCreatePost] = useState(false)

    // load item
    const loadBlogs = async ()=>{
        try {
            const result = await axios.get("http://localhost:5000/blogs")
            setBlogs(result.data)
        } catch (error) {
            console.error(error.message)
        }
    }

    useEffect(()=>{loadBlogs()},[])

    // add item
    function newPost(event) {
      const { name, value } = event.target;

      setBlog((prev) => {
        return { ...prev, [name]: value };
      });
    }

    const submitPost = async ()=>{
      try {
        const result = await axios.post("http://localhost:5000/blogs",{blog:blog})
        setBlogs([...blogs, result.data])
        setCreatePost(false)
      } catch (error) {
        console.error(error.message)
      }
    }

    // edit item
    async function editBlog(id,editedBlog){
        try {
            const result = await axios.put(`http://localhost:5000/blogs/${id}`,{text:editedBlog})
            let changed = blogs.map((item)=>item.id===id?result.data:item)
            setBlogs(changed)
        } catch (error) {
            console.error(error.message)
        }
    }

    // delete item
    async function deleteBlog(id){
        try {
            const result = await axios.delete(`http://localhost:5000/blogs/${id}`)
        } catch (error) {
            console.error(error.message)
        }
    }

    return(<div>
        <Header/>
        {(!createPost) && (
            <button onClick={()=>{setCreatePost(!createPost)}}>+ Create New Blog</button>
        )}
        {createPost && (
          <div>
            <input name="title" type="text" value={blog.title} onChange={newPost} placeholder="Title..." hidden></input>
            <textarea name="content" rows="5" value={blog.content} onChange={newPost} placeholder="Content..." hidden></textarea>
            <button onClick={()=>{submitPost()}}>Save</button>
            <button onClick={()=>{setCreatePost(!createPost)}}>Cancel</button>
           </div>
        )}

        <hr/>
        <h2>Blogs</h2>
        {blogs.map((blog)=>(
            <Blog id={blog.id} title={blog.title} content={blog.content} onDelete={deleteBlog} onEdit={editBlog}/>))}
        <Footer/> 
    </div>)
}

export default App
