import React, {useState, useEffect} from "react"
import axios from "axios"
import Header from "./Header"
import Footer from "./Footer"
import Blog from "./Blog"
import { create } from "domain"

function App(){

    const [blog,setBlog] = useState({title:"", content:""})
    const [blogs,setBlogs] = useState([])
    const [createPost, setCreatePost] = useState(false)
    const [postDone, setPostDone] = useState(true)

    // load item
    const loadBlogs = async ()=>{
        try {
            const result = await axios.get("http://localhost:5000/blogs")
            setBlogs(result.data)
        } catch (error) {
            console.error(error.message)
        }
    }

    useEffect(()=>{loadBlogs()},[])

    // add item
    function newPost(event) {
      const { name, value } = event.target;

      setBlog((prev) => {
        return { ...prev, [name]: value };
      });
    }
    // edit item
    // delete item



    return(<div>
        <Header/>
        {postDone && (
            <button onClick={()=>{setCreatePost(!createPost)}}>+ Create New Blog</button>
        )}
        {createPost && (
          <div>
            {setPostDone(!postDone)}
            <input name="title" type="text" value={title} onChange={newPost} placeholder="Title..." hidden></input>
            <textarea name="content" rows={5} value={content} onChange={newPost} placeholder="Content..." hidden></textarea>
            <button onClick={()=>{newPost()}}>Save</button>
            <button onClick={()=>{}}>Cancel</button>
           </div>
        )}

        <hr/>
        <h2>Blogs</h2>
        {blogs.map((blog)=>{<Blog/>})}
        <Footer/> 
    </div>)
}

export default App
